function copyTranslate(t,e){var r=document.querySelector("#"+t),o=document.querySelector("#"+e);r.value=o.value}function copyTranslateAll(){var t=document.querySelectorAll(".scTranslateRTL");for(var e of t)e.click()}function toggleRibbon(){var t=document.querySelector("#scCrossPiece"),e=document.querySelector("#scWebEditRibbon"),r=document.querySelector(".scExpTab").querySelector(".tabText");"display:none !important"!=e.getAttribute("style")?(t.setAttribute("style","height:0px !important"),e.setAttribute("style","display:none !important"),r.innerText="▼ Show"):(t.setAttribute("style","height:300px !important"),e.setAttribute("style","display:block !important"),r.innerText="▲ Hide")}